import React, { useState } from "react";

export default function ExpenseForm({ userId, onAdd }) {
  const [expense, setExpense] = useState("");
  const [price, setPrice] = useState("");
  const [category, setCategory] = useState("Інше");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!expense || !price) return alert("Заповніть всі поля!");

    try {
      const res = await fetch("http://localhost:8081/expenses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, expense, price: Number(price), category }),
      });
      const data = await res.json();

      if (res.ok) {
        onAdd({ id: data.expenseId, expense, price: Number(price), category });
        setExpense("");
        setPrice("");
        setCategory("Інше");
      } else {
        alert(data.error);
      }
    } catch (err) {
      console.error(err);
      alert("Помилка сервера");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={expense} onChange={e => setExpense(e.target.value)} placeholder="Назва витрати" />
      <input type="number" value={price} onChange={e => setPrice(e.target.value)} placeholder="Сума" />
      <select value={category} onChange={e => setCategory(e.target.value)}>
        <option value="Їжа">Їжа</option>
        <option value="Транспорт">Транспорт</option>
        <option value="Розваги">Розваги</option>
        <option value="Одяг">Одяг</option>
        <option value="Інше">Інше</option>
      </select>
      <button type="submit">Додати</button>
    </form>
  );
}
