import React, { useState } from "react";
import ExpenseForm from "../components/ExpenseForm";
import ExpenseList from "../components/ExpenseList";
import Total from "../components/Total";
import "./ExpenseFormPage.css";

export default function ExpenseFormPage({ expenses, setExpenses }) {
  const addExpense = (item) => {
    setExpenses([item, ...expenses]); // додаємо нову витрату зверху
  };

  const deleteExpense = (index) => {
    setExpenses(expenses.filter((_, i) => i !== index));
  };

  return (
    <div className="expense-page">
      <ExpenseForm onAdd={addExpense} />
      <ExpenseList list={expenses.slice(0, 10)} onDelete={deleteExpense} /> {/* максимум 10 */}
      <Total list={expenses} />
    </div>
  );
}
