import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Profile from "./pages/Profile";
import ExpenseFormPage from "./pages/ExpenseFormPage";
import ExpenseListPage from "./pages/ExpenseListPage";
import Header from "./components/Header";
import "./App.css";

export default function App() {
  const [user, setUser] = useState(null);
  const [expenses, setExpenses] = useState([]);

  return (
    <Router>
      {/* Header з’являється тільки після реєстрації / входу */}
      {user && <Header user={user} />}

      <div className="main-container">
        <Routes>
          {/* Початкова сторінка */}
          <Route path="/" element={user ? <Navigate to="/expense" /> : <Home />} />

          {/* Реєстрація / Логін */}
          <Route path="/register" element={<Register setUser={setUser} />} />
          <Route path="/login" element={<Login setUser={setUser} />} />

          {/* Захищені маршрути */}
          <Route
            path="/expense"
            element={
              user ? (
                <ExpenseFormPage expenses={expenses} setExpenses={setExpenses} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/all-expenses"
            element={
              user ? (
                <ExpenseListPage expenses={expenses} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/profile"
            element={user ? <Profile user={user} /> : <Navigate to="/login" />}
          />

          {/* Будь-який інший маршрут повертає на Home */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}
