import React, { useState, useEffect } from "react";
import ExpenseForm from "../components/ExpenseForm";
import ExpenseList from "../components/ExpenseList";

export default function ExpenseFormPage({ user }) {
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    if (!user) return; // Якщо користувача нема, нічого не робимо
    fetch(`http://localhost:8081/expenses/${user.id}`)
      .then((res) => res.json())
      .then((data) => setExpenses(data))
      .catch((err) => console.error(err));
  }, [user]);

  const addExpense = (item) => setExpenses([item, ...expenses]);

  if (!user) return <p>Завантаження користувача...</p>; // Покажемо поки user не завантажився

  return (
    <div>
      <h2>Мої витрати</h2>
      <ExpenseForm userId={user.id} onAdd={addExpense} />
      <ExpenseList list={expenses} />
    </div>
  );
}
