import React, { useState, useEffect } from "react";
import ExpenseForm from "../components/ExpenseForm";
import ExpenseList from "../components/ExpenseList";
import Total from "../components/Total";
import "./ExpenseFormPage.css";

export default function ExpenseFormPage({ user, expenses, setExpenses }) {
  useEffect(() => {
    // Завантажити витрати з сервера при вході
    if (!user) return;
    fetch(`http://localhost:8081/expenses/${user.id}`)
      .then(res => res.json())
      .then(data => setExpenses(data))
      .catch(err => console.error(err));
  }, [user]);

  const addExpense = async (item) => {
    if (!user) return;

    try {
      const res = await fetch("http://localhost:8081/expense", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...item, userId: user.id }),
      });
      const data = await res.json();
      if (res.ok) {
        setExpenses([ { ...item, id: data.id }, ...expenses ]);
      } else {
        alert(data.error || "Помилка при додаванні витрати");
      }
    } catch (err) {
      console.error(err);
    }
  };

  const deleteExpense = async (id) => {
    try {
      const res = await fetch(`http://localhost:8081/expense/${id}`, { method: "DELETE" });
      if (res.ok) {
        setExpenses(expenses.filter(e => e.id !== id));
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="expense-page">
      <ExpenseForm onAdd={addExpense} />
      <ExpenseList list={expenses.slice(0, 10)} onDelete={deleteExpense} />
      <Total list={expenses} />
    </div>
  );
}
