import React, { useState, useEffect } from "react";
import ExpenseForm from "../components/ExpenseForm";  // <-- поправлено шлях
import ExpenseList from "../components/ExpenseList";  // <-- поправлено шлях

export default function ExpenseFormPage({ user }) {
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    if (!user) return;
    fetch(`http://localhost:8081/expenses/${user.id}`)
      .then(res => res.json())
      .then(data => setExpenses(data))
      .catch(err => console.error(err));
  }, [user]);

  const addExpense = (expense) => setExpenses([expense, ...expenses]);
  const deleteExpense = (index) => setExpenses(expenses.filter((_, i) => i !== index));

  if (!user) return <p>Завантаження користувача...</p>;

  return (
    <div>
      <ExpenseForm userId={user.id} onAdd={addExpense} />
      <ExpenseList list={expenses} onDelete={deleteExpense} />
    </div>
  );
}
