import React from "react";

export default function ExpenseList({ list, onDelete }) {
  return (
    <ul>
      {list.length === 0 ? (
        <p>Поки що немає витрат</p>
      ) : (
        list.map((item, index) => (
          <li key={item.id || index}>
            {item.expense} - {item.price} грн ({item.category})
            {onDelete && <button onClick={() => onDelete(index)}>✕</button>}
          </li>
        ))
      )}
    </ul>
  );
}
